const Counter = (props) => {

  const [counter, setCounter] = (0)

  const handleClick = () => {
    setCounter(counter + 1)
  }

  return (
    <div>
      <p>
        The initial value of the counter is :
      </p>
      <h1>{counter}</h1>
      <button onClick={handleClick}> increment </button>
    </div>
  );
}

export default Counter
